package day01;

public class SwitchDemo01 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		switch (a) {
		case 1:
			System.out.println("a的值为" + a);
			break;
		case 2:
			System.out.println("a的值为" + a);
			break;
		case 3:
			System.out.println("a的值为" + a);
			break;
		default:
			System.out.println("a的值不在1-3之间");
			break;
		}
	}

}
